// Loop through each incoming item
for (let item of items) {
  const from = item.json.From;
  if (typeof from === 'string') {
    // If it’s in the form "Name <email@domain.com>", extract the part inside <>
    const match = from.match(/<([^>]+)>/);
    // If regex matched, use the captured email; otherwise leave it as-is
    item.json.From = match ? match[1] : from;
  }
}

// Return the modified items back to the workflow
return items;

/**
 * n8n Function node – Group e‑mails by thread + add dateTime
 * Output structure:
 * [
 *   {
 *     threadId: '…',
 *     emails: [
 *       { dateTime:'2025‑04‑19T17:56:43+10:00', from:'rep'|'customer', message:'…' },
 *       …
 *     ]
 *   },
 *   …
 * ]
 */

const REP_ADDRESSES = [
  'kiaghasem.dev@gmail.com'
].map(a => a.toLowerCase());

/* ---------- helpers ---------- */
function decodeBody(payload) {
  if (payload?.body?.data) return b64(payload.body.data);

  if (Array.isArray(payload?.parts)) {
    const part =
      payload.parts.find(p => p.mimeType === 'text/plain' && p.body?.data) ??
      payload.parts.find(p => p.mimeType === 'text/html'  && p.body?.data);
    if (part) return b64(part.body.data);
  }
  return '';
}

function b64(str) {
  const normalised = str.replace(/-/g, '+').replace(/_/g, '/');
  return Buffer.from(normalised, 'base64').toString('utf8');
}

function whoIsSender(fromHeader) {
  const match = fromHeader.match(/<([^>]+)>/);
  const email = (match ? match[1] : fromHeader).trim().toLowerCase();
  return REP_ADDRESSES.includes(email) ? 'rep' : 'customer';
}

/** Convert Gmail internalDate (ms since epoch) ➜ ISO‑8601 in Australia/Sydney */
function formatDateTime(internalDate) {
  const date = new Date(Number(internalDate));
  return new Intl.DateTimeFormat('en-AU', {
    timeZone: 'Australia/Sydney',
    year:   'numeric',
    month:  '2-digit',
    day:    '2-digit',
    hour:   '2-digit',
    minute: '2-digit',
    second: '2-digit',
    hour12: false,
  }).format(date)                         // "19/04/2025, 17:56:43"
    .replace(',', '')                     // remove comma
    .replace(/(\d{2})\/(\d{2})\/(\d{4})/, '$3-$2-$1') // to YYYY‑MM‑DD
    .replace(' ', 'T') + '+10:00';        // add ISO T separator + offset
}

/* ---------- main ---------- */
const rawItems = $input.all();
const threads = new Map();
const orderedThreads = [];

for (const { json } of rawItems) {
  const threadId = json.threadId;
  if (!threads.has(threadId)) {
    const newThread = { threadId, emails: [] };
    threads.set(threadId, newThread);
    orderedThreads.push(newThread);
  }

  const emailObj = {
    dateTime: formatDateTime(json.internalDate),
    from:     whoIsSender(json.From ?? json.from ?? ''),
    message:  decodeBody(json.payload),
  };

  threads.get(threadId).emails.push(emailObj);
}

return orderedThreads.map(t => ({ json: t }));

