/**
 * n8n - Function node
 * Converts the “notes” array on each item into a single Markdown string.
 * Resulting items look like:  { json: { notes: "Note#1:\n2025-04-26T03:56:06.568Z\n…\n\nNote#2:\n…" } }
 */

return items.map(item => {
	// Guard against missing or malformed data
	const noteArray = Array.isArray(item.json.notes) ? item.json.notes : [];

	//  Build the combined note string
	const combinedNotes = noteArray
		.map((note, idx) =>
			`Note#${idx + 1}:\n${note.dateAdded}\n${note.body || ''}`)
		.join('\n\n');          // blank line between notes

	//  Output a brand-new item that keeps only the “notes” field
	return {
		json: {
			notes: combinedNotes,
		},
	};
});

